function ff_psc_calculate()
{
 var amount = 0\x3B
 var order\x3B
 var count = 0\x3B

 // add base amout of regular size
 order = ff_getElementByName(\x27psc_custom_regular\x27).value\x3B
 if (order != \x27\x27) {
 order = parseInt(order)\x3B
 count += order\x3B
 amount += order * 5.99\x3B
 } // if
 
 // add base amount of large size
 order = ff_getElementByName(\x27psc_custom_large\x27).value\x3B
 if (order != \x27\x27) {
 order = parseInt(order)\x3B
 count += order\x3B
 amount += order * 8.99\x3B
 }

 // count number of adds
 var adds = 0\x3B
 for (var i = 0\x3B i \x3C ff_elements.length\x3B i++)
 if (ff_elements[i][2]==\x27psc_adds\x27) 
 if (ff_getElementByIndex(i).checked) adds++\x3B
 amount += count * adds * 0.49\x3B

 // show result
 ff_getElementByName(\x27psc_amount\x27).value = Math.round(amount*100)/100\x3B
} // ff_psc_calculate

