function ff_revoke_action(element\x2C action)
{
 // check if user is logged in
 var userid = ff_getElementByName(\x27userid\x27).value\x3B
 if (userid == 0) {
 alert(\x27You are not logged in to the site.\x27)\x3B
 return\x3B
 } // if

 // search if userid is in the list
 var listid = ff_getIdByName(\x27participants\x27)\x3B
 var qrows = ff_queryRows[listid]\x3B
 var cnt = qrows.length\x3B
 var i\x3B
 var rowid = 0\x3B
 for (i = 0\x3B i \x3C cnt\x3B i++)
 if (qrows[i][1] == userid) {
 rowid = qrows[i][0]\x3B
 break\x3B
 } // if
 if (rowid == 0) {
 alert(\x27You have currently no registration.\x27)\x3B
 return\x3B
 } // if

 if (!confirm(\x27Do you really want to revoke your registration?\x27)) return\x3B
 ff_getElementByName(\x27deleteid\x27).value = rowid\x3B
 ff_submitForm()\x3B
} // ff_revoke_action

