form.piece3code:
//+trace dis

// configuration variables
$moderate = 1\x3B // 0 = publish directly\x2C 1 = leave unpublished

// declare globals used in this piece
global $Itemid\x3B

// load the standard piece library
$this-\x3EexecPieceByName(\x27ff_InitLib\x27)\x3B

// get values from submission
$name = ff_getSubmit(\x27name\x27\x2C \x27\x27)\x3B
$title = ff_getSubmit(\x27title\x27\x2C \x27\x27)\x3B
$text = ff_getSubmit(\x27text\x27\x2C \x27\x27)\x3B
$section = ff_getSubmit(\x27section\x27\x2C 0)\x3B
$category = ff_getSubmit(\x27category\x27\x2C 0)\x3B

// get the userid of a superadministrator. the article will be created
// by the superadmin\x2C but use as author alias the name of the person
// who submitted it.
$creator = ff_select(\x22select id\x2C email\x2C sendEmail from #__users where gid=25\x22)\x3B
$creator = $creator[0]\x3B // take first row

// format text\x2C date and state
$text = ff_markdown($text)\x3B
$date = date( \x22Y-m-d H:i:s\x22 )\x3B
if ($moderate) $state = 0\x3B else $state = 1\x3B

// save new submission into #__content table
ff_query(
 \x22insert into #__content \x22.
 \x22(title\x2C title_alias\x2C introtext\x2C state\x2C sectionid\x2C \x22.
 \x22catid\x2C created\x2C created_by\x2C created_by_alias) \x22.
 \x22values \x22.
 \x22(\x27$title\x27\x2C \x27$title\x27\x2C \x27$text\x27\x2C \x27$state\x27\x2C \x27$section\x27\x2C \x22.
 \x22\x27$category\x27\x2C \x27$date\x27\x2C \x27$creator-\x3Eid\x27\x2C \x27$name\x27)\x22
)\x3B

// get section and cat name
$section_name = ff_selectValue(\x22select title from #__sections where id=\x27$section\x27\x22)\x3B
$cat_name = ff_selectValue(\x22select title from #__categories where id=\x27$category\x27\x22)\x3B

// update #__messages table for backend notices
$subject = \x22New content submitted\x22\x3B
$message = \x22A new product was submitted by [$name] \x22.
 \x22titled [$title] in section [$section_name] and category [$cat_name]\x22\x3B
ff_query(
 \x22insert into #__messages (user_id_from\x2C user_id_to\x2C date_time\x2C subject\x2Cmessage) \x22.
 \x22values (\x27\x22.JFactory::getUser()-\x3Eget(\x27id\x27\x2C0).\x22\x27\x2C \x27$creator-\x3Eid\x27\x2C \x27$date\x27\x2C \x27$subject\x27\x2C\x27$message\x27)\x22
